CREATE TABLE "public"."workspaces" ("id" serial NOT NULL, "name" text NOT NULL, "description" text, "workspace_type_id" integer NOT NULL, "amenities" text[] NOT NULL, "capacity" integer NOT NULL, "area" text NOT NULL, "street" text NOT NULL, "city_id" integer NOT NULL, "contact_no" text NOT NULL, "email" text NOT NULL, "website" text NOT NULL, "am" boolean NOT NULL DEFAULT false, "pm" boolean NOT NULL DEFAULT false, "from" timetz NOT NULL, "to" timetz NOT NULL, "active" boolean NOT NULL DEFAULT true, "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz NOT NULL DEFAULT now(), "deleted_at" timestamptz, PRIMARY KEY ("id") , FOREIGN KEY ("workspace_type_id") REFERENCES "public"."workspace_types"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("city_id") REFERENCES "public"."cities"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("name"));
